#include <string>
#include <set>
#include <unordered_set>
#include <map>
#include <unordered_map>
#include <iostream>
#include <vector>

using std::string;

class Solution {
public:
    bool checkInclusion(string s1, string s2) {
        /*
         * 方法1: 滑动窗口
         * 时间复杂度 O(n + mE) , 其中 E = 26
         * 空间复杂度 O(E)
         */
//        if (s1.length() > s2.length()) {
//            return false;
//        }
//        auto check = [](const std::vector<int> &arr) {
//            bool flag = std::all_of(arr.begin(), arr.end(), [](int n) { return n == 0; });
//            return flag;
//        };
//        std::vector<int> dp(26, 0);
//        int i = 0;
//        for (; i < s1.length(); ++i) {
//            --dp[s1[i] - 'a'];
//            ++dp[s2[i] - 'a'];
//        }
//        if (check(dp)) {
//            return true;
//        }
//        for (; i < s2.length(); ++i) {
//            ++dp[s2[i] - 'a'];
//            --dp[s2[i - s1.length()] - 'a'];
//            if (check(dp)) {
//                return true;
//            }
//        }
//        return false;

        /*
         * 方法2: 方法1的优化
         * 参考: https://leetcode.cn/problems/MPnaiL/solution/zi-fu-chuan-zhong-de-bian-wei-ci-ha-xi-b-9kii/
         */
        int m = s1.length(), n = s2.length();
        if (m > n) {
            return false;
        }
        std::vector<int> dp(26, 0);
        for (int i = 0; i < m; ++i) {
            --dp[s1[i] - 'a'];
            ++dp[s2[i] - 'a'];
        }
        int diff = std::count_if(dp.begin(), dp.end(), [](int n) { return n != 0; });
        if (diff == 0) {
            return true;
        }
        for (int i = m; i < n; ++i) {
            auto x = s2[i] - 'a';
            auto y = s2[i - m] - 'a';

            if (x == y) {
                continue;
            }

            if (dp[x] == 0) {
                ++diff;
            }
            ++dp[x];
            if (dp[x] == 0) {
                --diff;
            }

            if (dp[y] == 0) {
                ++diff;
            }
            --dp[y];
            if (dp[y] == 0) {
                --diff;
            }

            if (diff == 0) {
                return true;
            }
        }
        return false;
    }
};

int main() {
    std::cout << Solution().checkInclusion("ab", "eidbaooo") << std::endl;
    std::cout << Solution().checkInclusion("ab", "eidboaoo") << std::endl;
    std::cout << Solution().checkInclusion("hello", "ooolleoooleh") << std::endl;
    std::cout << Solution().checkInclusion(
            "",
            "")
              << std::endl;
    return 0;
}